body.script:
//
    document.addEventListener('DOMContentLoaded', function() {
        // Mobile menu toggle
        const mobileMenuButton = document.getElementById('mobile-menu-button');
        const mobileMenu = document.getElementById('mobile-menu');
        mobileMenuButton.addEventListener('click', () => {
            mobileMenu.classList.toggle('hidden');
        });

        // Active nav link on scroll
        const sections = document.querySelectorAll('section');
        const navLinks = document.querySelectorAll('.nav-link');

        window.onscroll = () => {
            let current = 'home'; // Default to home
            sections.forEach(section => {
                const sectionTop = section.offsetTop;
                if (pageYOffset >= sectionTop - 75) { // Adjusted offset for fixed header
                    current = section.getAttribute('id');
                }
            });

            navLinks.forEach(link => {
                link.classList.remove('active');
                if (link.getAttribute('href').includes(current)) {
                    link.classList.add('active');
                }
            });
        };

        // Quotes slider
        const quotes = [
            {
                text: "The only way to do great work is to love what you do. I found my love in the ink and the paper.",
                author: "The Last Starlight"
            },
            {
                text: "A story is a letter that the author writes to himself, to tell himself things that he would be unable to discover otherwise.",
                author: "Echoes of Yesterday"
            },
            {
                text: "In the chaos of the stars, she found her silence. In the silence, she found her voice.",
                author: "City of Glass Souls"
            }
        ];

        const quoteContainer = document.getElementById('quote-container');
        const dotsContainer = document.querySelector('#quotes .flex');
        let currentQuoteIndex = 0;

        function showQuote(index) {
            const quote = quotes[index];
            if (!quoteContainer) return; 

            quoteContainer.innerHTML = `
                <div class="quote-card absolute w-full left-0 top-0">
                    <p class="text-2xl md:text-3xl italic leading-relaxed">"${quote.text}"</p>
                    <p class="mt-4 text-lg font-semibold text-gray-400">&mdash; From ${quote.author}</p>
                </div>
            `;
             // Fade in
            setTimeout(() => {
                const quoteCard = quoteContainer.querySelector('.quote-card');
                if(quoteCard) quoteCard.style.opacity = '1';
            }, 50);

            // Update dots
            if (!dotsContainer) return;
            const dots = dotsContainer.querySelectorAll('button');
            dots.forEach((dot, dotIndex) => {
                if (dotIndex === index) {
                    dot.classList.add('bg-white');
                    dot.classList.remove('bg-gray-500');
                } else {
                    dot.classList.remove('bg-white');
                    dot.classList.add('bg-gray-500');
                }
            });
        }

        function nextQuote() {
            currentQuoteIndex = (currentQuoteIndex + 1) % quotes.length;
            showQuote(currentQuoteIndex);
        }

        // Initialize quotes
        if (quoteContainer &amp;&amp; dotsContainer) {
            quotes.forEach((_, index) => {
                const dot = document.createElement('button');
                dot.classList.add('w-3', 'h-3', 'rounded-full', 'transition-colors', 'bg-gray-500');
                dot.addEventListener('click', () => {
                    currentQuoteIndex = index;
                    showQuote(index);
                });
                dotsContainer.appendChild(dot);
            });

            showQuote(0);
            setInterval(nextQuote, 7000); // Change quote every 7 seconds
        }

        // Form submission
        const contactForm = document.querySelector('#contact form');
        if(contactForm) {
            contactForm.addEventListener('submit', function(e) {
                e.preventDefault();
                contactForm.innerHTML = `<div class="text-center p-8">
                    <h3 class="text-2xl font-bold mb-2">Thank you!</h3>
                    <p class="text-gray-600">Your message has been sent. I'll get back to you shortly.</p>
                </div>`;
            });
        }
    });
//
